using UnityEngine;public class Enhance_TapToDismiss : MonoBehaviour{    // Destroy ourself when the next input event occurs    void Update()    {        if (Input.GetMouseButtonDown(0) || Input.GetMouseButtonDown(1))        {            Destroy(gameObject);            return;        }        if (Input.touchCount > 0)        {            if (Input.GetTouch(0).phase == TouchPhase.Began)            {                Destroy(gameObject);                return;            }        }    }}